get_tratamentos([], []).
get_tratamentos([(D,P,E)|R], L) :- 
   %obter os tratamentos para as doencas
   get_tratamentos(R, R2),
   (
      (
         %associar o tratamento a uma variável 
         tratamento(D,T), 
         %submeter tratamento para a lista
         L = [(D,P,E,T)|R2]
      ); 
      L = R2
   ).
    %apresentar os resultados
apresentar_resultados([]).
apresentar_resultados([(D,P,E,T)|R]):-
   write('Pode ter '), write(D), write(' com '), write(P), write('% de certeza, '),  
   write(' e o tratamento = '), write(T),
   nl, apresentar_resultados(R).


